mapper.insert:
INSERT INTO t_notification (
    title, content, type, sender, recipient, status, importance, permission, 
    create_time, send_time, read_time, expire_time, extra_data
) VALUES (
    #{title}, #{content}, #{type}, #{sender}, #{recipient}, #{status}, #{importance}, #{permission}, 
    #{createTime}, #{sendTime}, #{readTime}, #{expireTime}, #{extraData}
)

mapper.select:
SELECT 
FROM t_notification
WHERE recipient = #{recipient}

    AND status = #{status}

ORDER BY create_time DESC
